import React from 'react'
import Questions1 from './Questions1'
import Questions2 from './Questions2'
import Questions3 from './Questions3'

export default class Questionnaire extends React.Component {
    constructor() {
        super()
        this.state = {
            currentStep: 1,
            selected: [],
            name: ''
        }
    }

    updateSelected = (e) => {
        if (![...this.state.selected].includes(e.target.id)) {
            this.setState({
                selected: [...this.state.selected, e.target.id]
            })
        } else {
            const index = [...this.state.selected].indexOf(e.target.id)
            console.log(index);
            this.setState({
                selected: [...this.state.selected.slice(index)]
            })
        }
    }

    addName = (name) => {
        this.setState({
            name: name
        })
    }

    onSendData = () => {
        fetch('/api', {
            method: 'POST',
            body: JSON.stringify(this.state.selected),
            headers: {
                'Content-Type': 'application/json'
            }
        }).then(response => console.log(response))
    }

    next = () => {
        console.log('next clicked');
    }

    prev = () => {
        console.log('prev clicked');
    }

    render() {
        // const CurrentStep = this.state.steps[this.state.currentStep - 1]
        console.log(this.state.currentStep);
        return (
            <div>
                <h1>Questionnaire</h1>
                <p>Tick the things you like</p>
                <Questions1 onUpdateSelected={ this.updateSelected } currentStep={this.currentStep} />
                <Questions2 onUpdateSelected={ this.updateSelected } currentStep={this.currentStep} />
                <Questions3 onUpdateSelected={ this.updateSelected } onDataSend={ this.onSendData } onNameChange={ this.addName} currentStep={this.currentStep} />
                <button onClick={this.next}>Next</button>
                <button onClick={this.prev}>Prev</button>
            </div>
        )
    }
}
